import { useState } from 'react';
import { useLocation } from 'wouter';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { useToast } from '@/hooks/use-toast';
import { IndustrySelector } from '@/components/rfq/industry-selector';
import { ProcessSelector } from '@/components/rfq/process-selector';
import { Configurator } from '@/components/rfq/configurator';
import { authenticatedApiClient } from '@/lib/supabase';
import { SKU } from '@shared/schema';
import { INDUSTRIES } from '@/lib/constants';

export default function CreateRFQ() {
  const [currentStep, setCurrentStep] = useState(1);
  const [selectedIndustry, setSelectedIndustry] = useState<string>('');
  const [selectedSKU, setSelectedSKU] = useState<SKU | null>(null);
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const createRFQMutation = useMutation({
    mutationFn: (rfqData: any) => 
      authenticatedApiClient.post('/api/protected/rfqs', rfqData),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/protected/rfqs'] });
      toast({
        title: "RFQ Created Successfully",
        description: "Your request for quotation has been submitted for review.",
      });
      setLocation('/buyer/rfqs');
    },
    onError: (error: any) => {
      toast({
        title: "Failed to Create RFQ",
        description: error.message || "Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleIndustrySelect = (industry: string) => {
    setSelectedIndustry(industry);
    setCurrentStep(2);
  };

  const handleProcessSelect = (sku: SKU) => {
    setSelectedSKU(sku);
    setCurrentStep(3);
  };

  const handleConfiguratorSubmit = (data: any) => {
    if (!selectedSKU) return;

    const rfqData = {
      title: data.itemTitle,
      details: {
        industry: selectedIndustry,
        sku: selectedSKU,
        items: [{ ...data, skuCode: selectedSKU.code }],
        files: data.files || [], // Include uploaded files
      },
      ndaRequired: data.ndaRequired,
      confidential: data.confidential,
      budgetRange: data.budgetMin && data.budgetMax ? {
        min: data.budgetMin,
        max: data.budgetMax,
      } : null,
    };

    console.log('ðŸ”¥ Submitting RFQ with data:', { 
      title: rfqData.title, 
      filesCount: data.files?.length || 0,
      industry: selectedIndustry 
    });

    createRFQMutation.mutate(rfqData);
  };

  const handleBack = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
      if (currentStep === 2) {
        setSelectedIndustry('');
      } else if (currentStep === 3) {
        setSelectedSKU(null);
      }
    } else {
      setLocation('/buyer/dashboard');
    }
  };

  const steps = [
    { number: 1, title: 'Industry' },
    { number: 2, title: 'Process' },
    { number: 3, title: 'Configurator' },
  ];

  const selectedIndustryData = INDUSTRIES.find(ind => ind.id === selectedIndustry);

  return (
    <div className="p-8">
      <Card className="max-w-6xl mx-auto">
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="text-2xl font-bold">Create New RFQ</CardTitle>
            <Button variant="outline" onClick={handleBack} data-testid="button-close">
              âœ•
            </Button>
          </div>

          {/* Step Indicator */}
          <div className="flex items-center space-x-4 pt-4">
            {steps.map((step, index) => (
              <div key={step.number} className="flex items-center space-x-2">
                <div
                  className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium ${
                    currentStep >= step.number
                      ? 'bg-primary text-primary-foreground'
                      : 'bg-muted text-muted-foreground'
                  }`}
                >
                  {step.number}
                </div>
                <span className={`text-sm ${currentStep >= step.number ? 'font-medium' : 'text-muted-foreground'}`}>
                  {step.title}
                </span>
                {index < steps.length - 1 && <div className="w-8 h-px bg-border"></div>}
              </div>
            ))}
          </div>
        </CardHeader>

        <CardContent>
          {currentStep === 1 && (
            <IndustrySelector
              selectedIndustry={selectedIndustry}
              onIndustrySelect={handleIndustrySelect}
            />
          )}

          {currentStep === 2 && selectedIndustry && (
            <div>
              <div className="mb-4">
                <p className="text-sm text-muted-foreground">
                  Selected Industry: <span className="font-medium">{selectedIndustryData?.name}</span>
                </p>
              </div>
              <ProcessSelector
                selectedIndustry={selectedIndustry}
                selectedProcess={selectedSKU?.code}
                onProcessSelect={handleProcessSelect}
              />
            </div>
          )}

          {currentStep === 3 && selectedSKU && (
            <div>
              <div className="mb-4">
                <p className="text-sm text-muted-foreground">
                  Selected Process: <span className="font-medium">{selectedSKU.processName}</span>
                </p>
              </div>
              <Configurator
                selectedSKU={selectedSKU}
                onSubmit={handleConfiguratorSubmit}
                onBack={handleBack}
              />
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/buyer/offers.tsx.content:
import { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Input } from '@/components/ui/input';
import { Clock, Shield, Award, CreditCard, ExternalLink, Calendar, DollarSign } from 'lucide-react';
import { authenticatedApiClient } from '@/lib/supabase';
import { formatCurrency } from '@/lib/utils';
import { useToast } from '@/hooks/use-toast';
import { CuratedOffer } from '@shared/schema';
import { useLocation } from 'wouter';
import { queryClient, apiRequest } from '@/lib/queryClient';

// Interface for offer structure used in the component
interface OfferData {
  id: string;
  rfqId: string;
  rfqTitle: string;
  type: string;
  price: number;
  unitPrice?: number;
  quantity?: number;
  leadTime: number;
  warranty: string;
  quality: string;
  features: string[];
  recommended: boolean;
}

export default function BuyerOffers() {
  const [selectedOffer, setSelectedOffer] = useState<OfferData | null>(null);
  const [showPaymentDialog, setShowPaymentDialog] = useState(false);
  const [paymentRef, setPaymentRef] = useState('');
  const { toast } = useToast();
  const [, setLocation] = useLocation();

  // Fetch actual offers from API
  const { data: offers = [], isLoading } = useQuery({
    queryKey: ['/api/protected/buyer/offers'],
    queryFn: () => authenticatedApiClient.get('/api/protected/buyer/offers'),
  });

  // Transform API offers to match component interface
  const displayOffers = offers.map((offer: any) => ({
    id: offer.id,
    rfqId: offer.rfqId,
    rfqTitle: offer.rfq?.title || offer.rfq?.description || 'RFQ',
    type: offer.title || 'Standard',
    price: parseFloat(offer.totalPrice || '0'), // Use totalPrice for total order value
    unitPrice: parseFloat(offer.unitPrice || offer.details?.unitPrice || '0'), // Keep unit price separate
    quantity: offer.quantity || 100,
    leadTime: offer.details?.leadTime || offer.details?.leadTimeDays || 14,
    warranty: offer.details?.warranty || '6 months',
    quality: offer.details?.qualityAssurance || 'Standard quality check',
    features: offer.details?.features || ['Quality Assured', 'On-time Delivery'],
    recommended: false,
    paymentLink: offer.paymentLink, // Include payment link from admin
  }));

  // Using shared formatCurrency utility from @/lib/utils

  const calculatePaymentBreakdown = (totalPrice: number, advancePercentage: number = 30) => {
    const advanceAmount = Math.round(totalPrice * (advancePercentage / 100));
    const finalAmount = totalPrice - advanceAmount;
    return { advanceAmount, finalAmount, totalPrice };
  };

  const handleAcceptOffer = (offer: OfferData) => {
    setSelectedOffer(offer);
    setShowPaymentDialog(true);
  };

  const handlePaymentAction = async (offer: OfferData, paymentType: 'advance' | 'final') => {
    try {
      // Get the payment link from the offer
      const paymentLink = (offer as any).paymentLink;

      if (paymentLink) {
        // Open payment link in new tab
        window.open(paymentLink, '_blank');
        toast({
          title: "Payment Link Opened",
          description: "Complete payment and then mark as paid below.",
        });
      } else {
        toast({
          title: "No Payment Link",
          description: "Contact admin for payment instructions.",
          variant: "destructive",
        });
      }
    } catch (error) {
      toast({
        title: "Payment Failed",
        description: "Unable to open payment link. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleMarkAsPaid = async (offer: OfferData, transactionRef: string) => {
    try {
      // Calculate 30% advance payment
      const advanceAmount = Math.round(offer.price * 0.30);

      const transactionData = {
        curatedOfferId: offer.id,
        status: 'completed',
        transactionRef: transactionRef || `EXTERNAL-${Date.now()}`,
        amount: advanceAmount.toString(), // Convert to string as expected by schema
        netAmount: advanceAmount.toString(), // Net amount is same as amount for advance payment
        transactionType: 'advance_payment' as const, // Required field
        payerId: '', // Will be filled by backend with current user ID
        paymentMethod: 'external'
      };

      // Use apiRequest following project conventions
      await apiRequest('POST', '/api/protected/payment-transactions', transactionData);

      // Invalidate relevant caches
      await queryClient.invalidateQueries({ queryKey: ['/api/protected/buyer/offers'] });
      await queryClient.invalidateQueries({ queryKey: ['/api/protected/orders'] });

      toast({
        title: "Payment Recorded!",
        description: "Your payment has been marked as completed. Order will be created automatically.",
      });

      setShowPaymentDialog(false);

      // Navigate immediately after successful payment
      setLocation('/buyer/orders');

    } catch (error) {
      console.error('Payment recording error:', error);
      toast({
        title: "Failed to Record Payment",
        description: "Unable to record payment status. Please contact support.",
        variant: "destructive",
      });
    }
  };

  return (
    <div className="p-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-foreground">Offers</h1>
        <p className="text-muted-foreground mt-2">Review and accept offers from the marketplace</p>
      </div>

      <div className="space-y-6">
        {displayOffers.length === 0 ? (
          <Card>
            <CardContent className="p-8 text-center">
              <p className="text-muted-foreground">No offers available at the moment.</p>
              <p className="text-sm text-muted-foreground mt-2">
                Offers will appear here once admins publish them for your RFQs.
              </p>
            </CardContent>
          </Card>
        ) : (
          displayOffers.map((offer: OfferData) => (
            <Card key={offer.rfqId}>
              <CardHeader>
                <CardTitle>{offer.rfqTitle}</CardTitle>
                <p className="text-sm text-muted-foreground">
                  Received curated offers â€¢ Review and accept to proceed
                </p>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
                  <Card 
                    key={offer.id} 
                    className={`relative ${offer.recommended ? 'border-primary shadow-lg' : ''}`}
                    data-testid={`card-offer-${offer.id}`}
                  >
                    {offer.recommended && (
                      <div className="absolute -top-3 left-1/2 transform -translate-x-1/2">
                        <Badge className="bg-green-100 text-green-800">
                          <Award className="h-3 w-3 mr-1" />
                          Best Value
                        </Badge>
                      </div>
                    )}

                    <CardHeader className="text-center">
                      <CardTitle className="text-lg">{offer.type} Offer</CardTitle>
                      <div className="text-2xl font-bold">{formatCurrency(offer.unitPrice || offer.price / (offer.quantity || 100))}</div>
                      <p className="text-sm text-muted-foreground">per piece</p>
                    </CardHeader>

                    <CardContent className="space-y-4">
                      <div className="space-y-3">
                        <div className="flex items-center space-x-2">
                          <Clock className="h-4 w-4 text-muted-foreground" />
                          <span className="text-sm">Lead Time: {offer.leadTime} days</span>
                        </div>

                        <div className="flex items-center space-x-2">
                          <Shield className="h-4 w-4 text-muted-foreground" />
                          <span className="text-sm">Warranty: {offer.warranty}</span>
                        </div>

                        <div>
                          <p className="text-sm font-medium">Quality Assurance:</p>
                          <p className="text-sm text-muted-foreground">{offer.quality}</p>
                        </div>

                        <div>
                          <p className="text-sm font-medium mb-2">Features:</p>
                          <div className="flex flex-wrap gap-1">
                            {offer.features.map((feature: string, index: number) => (
                              <Badge key={index} variant="secondary" className="text-xs">
                                {feature}
                              </Badge>
                            ))}
                          </div>
                        </div>
                      </div>

                      {/* Payment Information */}
                      <div className="bg-blue-50 dark:bg-blue-900/20 p-3 rounded-lg border border-blue-200 dark:border-blue-800 mb-4">
                        <div className="flex items-center space-x-2 mb-2">
                          <CreditCard className="h-4 w-4 text-blue-600" />
                          <h4 className="font-medium text-blue-900 dark:text-blue-100">Payment Structure</h4>
                        </div>
                        <div className="grid grid-cols-2 gap-2 text-sm">
                          <div>
                            <p className="text-blue-700 dark:text-blue-300">Advance (30%)</p>
                            <p className="font-bold text-blue-900 dark:text-blue-100">
                              {formatCurrency(calculatePaymentBreakdown(offer.price).advanceAmount)}
                            </p>
                          </div>
                          <div>
                            <p className="text-blue-700 dark:text-blue-300">Final (70%)</p>
                            <p className="font-bold text-blue-900 dark:text-blue-100">
                              {formatCurrency(calculatePaymentBreakdown(offer.price).finalAmount)}
                            </p>
                          </div>
                        </div>
                        <div className="mt-2 pt-2 border-t border-blue-300 dark:border-blue-700">
                          <div className="flex items-center justify-between">
                            <span className="text-blue-700 dark:text-blue-300 text-sm">Total Order Value</span>
                            <span className="font-bold text-blue-900 dark:text-blue-100">
                              {formatCurrency(offer.price)}
                            </span>
                          </div>
                        </div>
                      </div>

                      <div className="pt-4 border-t">
                        <p className="text-xs text-muted-foreground mb-3 text-center">
                          Fulfilled by Logicwerk Marketplace
                        </p>
                        <Button 
                          className="w-full"
                          variant={offer.recommended ? "default" : "outline"}
                          onClick={() => handleAcceptOffer(offer)}
                          data-testid={`button-accept-offer-${offer.id}`}
                        >
                          <CreditCard className="h-4 w-4 mr-2" />
                          Accept Offer & Pay
                        </Button>
                      </div>
                    </CardContent>
                  </Card>
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>

      {/* Payment Dialog */}
      <Dialog open={showPaymentDialog} onOpenChange={setShowPaymentDialog}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>Complete Payment</DialogTitle>
          </DialogHeader>

          {selectedOffer && (
            <div className="space-y-4">
              <div className="bg-muted/50 p-4 rounded-lg">
                <h3 className="font-medium mb-2">{selectedOffer.type} Offer</h3>
                <p className="text-sm text-muted-foreground mb-2">
                  RFQ: {selectedOffer.rfqTitle}
                </p>
                <div className="text-2xl font-bold">{formatCurrency(selectedOffer.price)}</div>
                <p className="text-sm text-muted-foreground">Total Order Value</p>
              </div>

              <div className="space-y-3">
                <div className="flex items-center justify-between p-3 border rounded-lg">
                  <div>
                    <p className="font-medium">Advance Payment (30%)</p>
                    <p className="text-sm text-muted-foreground">Required to start production</p>
                  </div>
                  <div className="text-right">
                    <p className="font-bold">{formatCurrency(calculatePaymentBreakdown(selectedOffer.price).advanceAmount)}</p>
                    <Button 
                      size="sm" 
                      onClick={() => handlePaymentAction(selectedOffer, 'advance')}
                      data-testid="button-pay-advance"
                    >
                      <ExternalLink className="h-3 w-3 mr-1" />
                      Pay Now
                    </Button>
                  </div>
                </div>

                <div className="flex items-center justify-between p-3 border rounded-lg bg-muted/30">
                  <div>
                    <p className="font-medium text-muted-foreground">Final Payment (70%)</p>
                    <p className="text-sm text-muted-foreground">Due on delivery</p>
                  </div>
                  <div className="text-right">
                    <p className="font-bold text-muted-foreground">{formatCurrency(calculatePaymentBreakdown(selectedOffer.price).finalAmount)}</p>
                    <p className="text-xs text-muted-foreground">Pay later</p>
                  </div>
                </div>
              </div>

              <div className="bg-yellow-50 dark:bg-yellow-900/20 p-3 rounded-lg border border-yellow-200 dark:border-yellow-800">
                <div className="flex items-center space-x-2">
                  <Calendar className="h-4 w-4 text-yellow-600" />
                  <p className="text-sm font-medium text-yellow-800 dark:text-yellow-200">
                    Payment deadline: 7 days from acceptance
                  </p>
