            <input 
              type="file" 
              ref={gstRef} 
              style={{ display: 'none' }} 
              accept=".pdf,.jpg,.jpeg,.png" 
            />
            <input 
              type="file" 
              ref={bankRef} 
              style={{ display: 'none' }} 
              accept=".pdf,.jpg,.jpeg,.png" 
            />
            <input 
              type="file" 
              ref={isoRef} 
              style={{ display: 'none' }} 
              accept=".pdf,.jpg,.jpeg,.png" 
            />
            <input 
              type="file" 
              ref={samplesRef} 
              style={{ display: 'none' }} 
              accept=".jpg,.jpeg,.png" 
