                    size="sm" 
                    data-testid="button-upload-registration"
                    onClick={() => handleFileUpload('company_registration', registrationRef)}
                    disabled={uploadDocumentMutation.isPending}
                  >
                    {uploadedDocs.company_registration ? 'Replace File' : 'Upload File'}
