                  <Button 
                    variant="outline" 
                    size="sm" 
                    data-testid="button-upload-gst"
                    onClick={() => handleFileUpload('gst_certificate', gstRef)}
                    disabled={uploadDocumentMutation.isPending}
                  >
                    {uploadedDocs.gst_certificate ? 'Replace File' : 'Upload File'}
