import { useState, useEffect } from 'react';
import { Bell, Check, X, Clock } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Popover, PopoverContent, PopoverTrigger } from '@/components/ui/popover';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Separator } from '@/components/ui/separator';
import { Skeleton } from '@/components/ui/skeleton';
import { useQuery, useMutation } from '@tanstack/react-query';
import { apiRequest, queryClient } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';
import { formatDistanceToNow } from 'date-fns';

interface Notification {
  id: string;
  userId: string;
  type: string;
  title: string;
  message: string;
  isRead: boolean;
  metadata?: any;
  entityId?: string;
  entityType?: string;
  createdAt: string;
}

interface NotificationDropdownProps {
  className?: string;
}

export function NotificationDropdown({ className }: NotificationDropdownProps) {
  const [open, setOpen] = useState(false);
  const { toast } = useToast();

  // Fetch notifications
  const { data: notifications = [], isLoading: notificationsLoading, error: notificationsError } = useQuery<Notification[]>({
    queryKey: ['/api/protected/notifications'],
    enabled: open, // Only fetch when dropdown is open
  });

  // Fetch unread count
  const { data: countData, isLoading: countLoading } = useQuery<{ unreadCount: number }>({
    queryKey: ['/api/protected/notifications/count'],
    refetchInterval: 30000, // Refetch every 30 seconds
  });

  const unreadCount = countData?.unreadCount || 0;

  // Mark notification as read
  const markAsReadMutation = useMutation({
    mutationFn: (notificationId: string) => 
      apiRequest('PUT', `/api/protected/notifications/${notificationId}/read`),
    onSuccess: () => {
      // Invalidate both notifications and count queries
      queryClient.invalidateQueries({ queryKey: ['/api/protected/notifications'] });
      queryClient.invalidateQueries({ queryKey: ['/api/protected/notifications/count'] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: "Failed to mark notification as read",
        variant: "destructive",
      });
    }
  });

  // Mark all notifications as read
  const markAllAsReadMutation = useMutation({
    mutationFn: () => 
      apiRequest('PUT', '/api/protected/notifications/read-all'),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/protected/notifications'] });
      queryClient.invalidateQueries({ queryKey: ['/api/protected/notifications/count'] });
      toast({
        title: "Success",
        description: "All notifications marked as read",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: "Failed to mark all notifications as read",
        variant: "destructive",
      });
    }
  });

  const handleNotificationClick = (notification: Notification) => {
    if (!notification.isRead) {
      markAsReadMutation.mutate(notification.id);
    }
  };

  const handleMarkAllAsRead = () => {
    markAllAsReadMutation.mutate();
  };

  const getNotificationIcon = (type: string) => {
    switch (type) {
      case 'rfq_submitted':
      case 'rfq_approved':
      case 'rfq_status_change':
        return 'ðŸ“‹';
      case 'quote_received':
      case 'quote_accepted':
      case 'quote_rejected':
        return 'ðŸ’°';
      case 'supplier_invitation':
      case 'supplier_verified':
        return 'ðŸ¢';
      case 'order_created':
      case 'order_status_change':
        return 'ðŸ“¦';
      case 'production_update':
        return 'âš™ï¸';
      case 'inspection_completed':
        return 'âœ…';
      case 'payout_processed':
        return 'ðŸ’³';
      default:
        return 'ðŸ“¨';
    }
  };

  const formatNotificationType = (type: string) => {
    return type.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase());
  };

  return (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <Button
          variant="secondary"
          size="sm"
          className={`relative ${className || ''}`}
          data-testid="button-notifications"
        >
          <Bell className="h-4 w-4 mr-2" />
          {unreadCount > 0 && (
            <Badge 
              variant="destructive" 
              className="absolute -top-2 -right-2 h-5 w-5 flex items-center justify-center p-0 text-xs"
            >
              {unreadCount > 99 ? '99+' : unreadCount}
            </Badge>
          )}
        </Button>
      </PopoverTrigger>
      <PopoverContent 
        className="w-96 p-0" 
        align="end" 
        data-testid="notifications-dropdown"
      >
        <div className="flex items-center justify-between p-4 border-b">
          <h4 className="font-semibold text-sm">Notifications</h4>
          {notifications.length > 0 && (
            <Button
              variant="ghost"
              size="sm"
              onClick={handleMarkAllAsRead}
              disabled={markAllAsReadMutation.isPending}
              className="text-xs"
              data-testid="button-mark-all-read"
            >
              <Check className="h-3 w-3 mr-1" />
              Mark all read
            </Button>
          )}
        </div>

        <ScrollArea className="h-96">
          {notificationsLoading ? (
            <div className="p-4 space-y-3">
              {Array.from({ length: 3 }).map((_, index) => (
                <div key={index} className="space-y-2">
                  <Skeleton className="h-4 w-3/4" />
                  <Skeleton className="h-3 w-full" />
                  <Skeleton className="h-3 w-1/2" />
                </div>
              ))}
            </div>
          ) : notificationsError ? (
            <div className="p-4 text-center text-sm text-muted-foreground">
              <X className="h-8 w-8 mx-auto mb-2 text-destructive" />
              Failed to load notifications
            </div>
          ) : notifications.length === 0 ? (
            <div className="p-8 text-center text-sm text-muted-foreground">
              <Bell className="h-8 w-8 mx-auto mb-2" />
              No notifications yet
            </div>
          ) : (
            <div className="space-y-1 p-2">
              {notifications.map((notification: Notification) => (
                <Card 
                  key={notification.id}
                  className={`cursor-pointer transition-colors hover:bg-muted/50 ${
                    notification.isRead ? 'opacity-70' : 'bg-blue-50/50 dark:bg-blue-950/20'
                  }`}
                  onClick={() => handleNotificationClick(notification)}
                  data-testid={`notification-${notification.id}`}
                >
                  <CardContent className="p-3">
                    <div className="flex items-start space-x-3">
                      <div className="text-lg flex-shrink-0">
                        {getNotificationIcon(notification.type)}
                      </div>
                      <div className="flex-1 min-w-0">
                        <div className="flex items-center justify-between mb-1">
                          <h5 className="font-medium text-sm truncate">
                            {notification.title}
                          </h5>
                          {!notification.isRead && (
                            <div className="w-2 h-2 bg-blue-500 rounded-full flex-shrink-0 ml-2" />
                          )}
                        </div>
                        <p className="text-xs text-muted-foreground mb-2 line-clamp-2">
                          {notification.message}
                        </p>
                        <div className="flex items-center justify-between">
                          <Badge variant="secondary" className="text-xs">
                            {formatNotificationType(notification.type)}
                          </Badge>
                          <span className="text-xs text-muted-foreground flex items-center">
                            <Clock className="h-3 w-3 mr-1" />
                            {formatDistanceToNow(new Date(notification.createdAt), { addSuffix: true })}
                          </span>
                        </div>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </ScrollArea>

        {notifications.length > 0 && (
          <>
            <Separator />
            <div className="p-3 text-center">
              <Button
                variant="ghost"
                size="sm"
                className="text-xs w-full"
                onClick={() => setOpen(false)}
                data-testid="button-close-notifications"
              >
                Close
              </Button>
            </div>
          </>
        )}
      </PopoverContent>
    </Popover>
  );
}

client/src/pages/admin/payment-config.ts.content:
import { useState } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import { 
  CreditCard, Settings, DollarSign, Percent, 
  Plus, Edit, Trash2, Save, X, CheckCircle
} from 'lucide-react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Switch } from '@/components/ui/switch';
import { Badge } from '@/components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { useToast } from '@/hooks/use-toast';
import { authenticatedApiClient } from '@/lib/supabase';
import { queryClient } from '@/lib/queryClient';

interface PaymentMethod {
  id: string;
  name: string;
  type: 'razorpay' | 'bank_transfer' | 'upi' | 'wallet' | 'other';
  isActive: boolean;
  configuration: any;
  displayName: string;
  description?: string;
  processingFeePercent: string;
  createdAt: string;
  updatedAt: string;
}

interface PaymentConfiguration {
  id: string;
  configType: 'advance_percentage' | 'commission_rate' | 'platform_fee' | 'gateway_fee';
  value: string;
  description?: string;
  isActive: boolean;
  createdAt: string;
  updatedAt: string;
}

export default function PaymentConfigPage() {
  const { toast } = useToast();
  const [activeTab, setActiveTab] = useState('methods');
  const [showMethodDialog, setShowMethodDialog] = useState(false);
  const [showConfigDialog, setShowConfigDialog] = useState(false);
  const [editingMethod, setEditingMethod] = useState<PaymentMethod | null>(null);
  const [editingConfig, setEditingConfig] = useState<PaymentConfiguration | null>(null);

  // Payment Methods Data
  const { data: paymentMethods = [] } = useQuery({
    queryKey: ['/api/protected/admin/payment-methods'],
    queryFn: () => authenticatedApiClient.get('/api/protected/admin/payment-methods'),
  });

  // Payment Configurations Data
  const { data: paymentConfigs = [] } = useQuery({
    queryKey: ['/api/protected/admin/payment-configurations'],
    queryFn: () => authenticatedApiClient.get('/api/protected/admin/payment-configurations'),
  });

  // Payment Method Mutations
  const createMethodMutation = useMutation({
    mutationFn: (data: any) => authenticatedApiClient.post('/api/protected/admin/payment-methods', data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/protected/admin/payment-methods'] });
      setShowMethodDialog(false);
      setEditingMethod(null);
      toast({ title: 'Success', description: 'Payment method created successfully' });
    },
    onError: () => {
      toast({ title: 'Error', description: 'Failed to create payment method', variant: 'destructive' });
    }
  });

  const updateMethodMutation = useMutation({
    mutationFn: ({ id, ...data }: any) => authenticatedApiClient.put(`/api/protected/admin/payment-methods/${id}`, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/protected/admin/payment-methods'] });
      setShowMethodDialog(false);
      setEditingMethod(null);
      toast({ title: 'Success', description: 'Payment method updated successfully' });
    },
    onError: () => {
      toast({ title: 'Error', description: 'Failed to update payment method', variant: 'destructive' });
    }
  });

  const deleteMethodMutation = useMutation({
    mutationFn: (id: string) => authenticatedApiClient.delete(`/api/protected/admin/payment-methods/${id}`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/protected/admin/payment-methods'] });
      toast({ title: 'Success', description: 'Payment method deleted successfully' });
    },
    onError: () => {
      toast({ title: 'Error', description: 'Failed to delete payment method', variant: 'destructive' });
    }
  });

  // Payment Configuration Mutations
  const createConfigMutation = useMutation({
    mutationFn: (data: any) => authenticatedApiClient.post('/api/protected/admin/payment-configurations', data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/protected/admin/payment-configurations'] });
      setShowConfigDialog(false);
      setEditingConfig(null);
      toast({ title: 'Success', description: 'Payment configuration created successfully' });
    },
    onError: () => {
      toast({ title: 'Error', description: 'Failed to create payment configuration', variant: 'destructive' });
    }
  });

  const updateConfigMutation = useMutation({
    mutationFn: ({ id, ...data }: any) => authenticatedApiClient.put(`/api/protected/admin/payment-configurations/${id}`, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/protected/admin/payment-configurations'] });
      setShowConfigDialog(false);
      setEditingConfig(null);
      toast({ title: 'Success', description: 'Payment configuration updated successfully' });
    },
    onError: () => {
      toast({ title: 'Error', description: 'Failed to update payment configuration', variant: 'destructive' });
    }
  });

  const handleMethodSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);

    const data = {
      name: formData.get('name'),
      type: formData.get('type'),
      displayName: formData.get('displayName'),
      description: formData.get('description'),
      processingFeePercent: formData.get('processingFeePercent'),
      isActive: formData.get('isActive') === 'on',
      configuration: {
        apiKey: formData.get('apiKey'),
        secretKey: formData.get('secretKey'),
        webhookSecret: formData.get('webhookSecret'),
      }
    };

    if (editingMethod) {
      updateMethodMutation.mutate({ id: editingMethod.id, ...data });
    } else {
      createMethodMutation.mutate(data);
    }
  };

  const handleConfigSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);

    const data = {
      configType: formData.get('configType'),
      value: formData.get('value'),
      description: formData.get('description'),
      isActive: formData.get('isActive') === 'on',
    };

    if (editingConfig) {
      updateConfigMutation.mutate({ id: editingConfig.id, ...data });
    } else {
      createConfigMutation.mutate(data);
    }
  };

  const getMethodTypeIcon = (type: string) => {
    switch (type) {
      case 'razorpay': return <CreditCard className="h-4 w-4" />;
      case 'bank_transfer': return <DollarSign className="h-4 w-4" />;
      case 'upi': return <Settings className="h-4 w-4" />;
      default: return <CreditCard className="h-4 w-4" />;
    }
  };

  const getConfigTypeLabel = (type: string) => {
    switch (type) {
      case 'advance_percentage': return 'Advance Payment %';
      case 'commission_rate': return 'Commission Rate %';
      case 'platform_fee': return 'Platform Fee %';
      case 'gateway_fee': return 'Gateway Fee %';
      default: return type;
    }
  };

  return (
    <div className="p-8 space-y-8">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-foreground">Payment Configuration</h1>
          <p className="text-muted-foreground mt-2">Manage payment methods, gateways, and fee structures</p>
        </div>
      </div>

      {/* Configuration Tabs */}
      <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-6">
        <TabsList className="grid w-full grid-cols-3">
          <TabsTrigger value="methods" data-testid="tab-payment-methods">Payment Methods</TabsTrigger>
          <TabsTrigger value="configurations" data-testid="tab-payment-configurations">Fee Structure</TabsTrigger>
          <TabsTrigger value="overview" data-testid="tab-payment-overview">Overview</TabsTrigger>
        </TabsList>

        {/* Payment Methods Tab */}
        <TabsContent value="methods" className="space-y-6">
          <div className="flex items-center justify-between">
            <h2 className="text-2xl font-semibold">Payment Methods</h2>
            <Dialog open={showMethodDialog} onOpenChange={setShowMethodDialog}>
              <DialogTrigger asChild>
                <Button onClick={() => setEditingMethod(null)} data-testid="button-add-payment-method">
                  <Plus className="h-4 w-4 mr-2" />
                  Add Payment Method
                </Button>
              </DialogTrigger>
              <DialogContent className="max-w-2xl">
                <DialogHeader>
                  <DialogTitle>
                    {editingMethod ? 'Edit Payment Method' : 'Add Payment Method'}
                  </DialogTitle>
                </DialogHeader>
                <form onSubmit={handleMethodSubmit} className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="name">Internal Name</Label>
                      <Input
                        id="name"
                        name="name"
                        defaultValue={editingMethod?.name}
                        placeholder="razorpay_primary"
                        required
                        data-testid="input-method-name"
                      />
                    </div>
                    <div>
                      <Label htmlFor="type">Method Type</Label>
                      <Select name="type" defaultValue={editingMethod?.type}>
                        <SelectTrigger data-testid="select-method-type">
                          <SelectValue placeholder="Select method type" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="razorpay">Razorpay</SelectItem>
                          <SelectItem value="bank_transfer">Bank Transfer</SelectItem>
                          <SelectItem value="upi">UPI</SelectItem>
                          <SelectItem value="wallet">Wallet</SelectItem>
                          <SelectItem value="other">Other</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>
                  </div>
                  <div>
                    <Label htmlFor="displayName">Display Name</Label>
                    <Input
                      id="displayName"
                      name="displayName"
                      defaultValue={editingMethod?.displayName}
                      placeholder="Credit/Debit Card via Razorpay"
                      required
                      data-testid="input-display-name"
                    />
                  </div>
                  <div>
                    <Label htmlFor="description">Description</Label>
                    <Textarea
                      id="description"
                      name="description"
                      defaultValue={editingMethod?.description}
                      placeholder="Secure payment processing via Razorpay gateway"
                      data-testid="textarea-description"
                    />
                  </div>
                  <div>
                    <Label htmlFor="processingFeePercent">Processing Fee (%)</Label>
                    <Input
                      id="processingFeePercent"
                      name="processingFeePercent"
                      type="number"
                      step="0.01"
                      defaultValue={editingMethod?.processingFeePercent}
                      placeholder="2.5"
                      data-testid="input-processing-fee"
                    />
                  </div>

                  {/* Gateway Configuration */}
                  <div className="border-t pt-4">
                    <h4 className="font-medium mb-4">Gateway Configuration</h4>
                    <div className="space-y-4">
                      <div>
                        <Label htmlFor="apiKey">API Key</Label>
                        <Input
                          id="apiKey"
                          name="apiKey"
                          type="password"
                          defaultValue={editingMethod?.configuration?.apiKey}
                          placeholder="API Key"
                          data-testid="input-api-key"
                        />
                      </div>
                      <div>
                        <Label htmlFor="secretKey">Secret Key</Label>
                        <Input
                          id="secretKey"
                          name="secretKey"
                          type="password"
                          defaultValue={editingMethod?.configuration?.secretKey}
                          placeholder="Secret Key"
                          data-testid="input-secret-key"
                        />
                      </div>
                      <div>
                        <Label htmlFor="webhookSecret">Webhook Secret</Label>
                        <Input
                          id="webhookSecret"
                          name="webhookSecret"
                          type="password"
                          defaultValue={editingMethod?.configuration?.webhookSecret}
                          placeholder="Webhook Secret"
                          data-testid="input-webhook-secret"
                        />
                      </div>
                    </div>
                  </div>

                  <div className="flex items-center space-x-2">
                    <Switch
                      id="isActive"
                      name="isActive"
                      defaultChecked={editingMethod?.isActive ?? true}
                      data-testid="switch-method-active"
                    />
                    <Label htmlFor="isActive">Active</Label>
                  </div>

                  <div className="flex justify-end space-x-2">
                    <Button
                      type="button"
                      variant="outline"
                      onClick={() => setShowMethodDialog(false)}
                      data-testid="button-cancel-method"
                    >
                      Cancel
                    </Button>
                    <Button
                      type="submit"
                      disabled={createMethodMutation.isPending || updateMethodMutation.isPending}
                      data-testid="button-save-method"
                    >
                      <Save className="h-4 w-4 mr-2" />
                      {editingMethod ? 'Update' : 'Create'}
                    </Button>
                  </div>
                </form>
              </DialogContent>
            </Dialog>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {paymentMethods.map((method: PaymentMethod) => (
              <Card key={method.id} className="relative">
                <CardHeader className="pb-3">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-2">
                      {getMethodTypeIcon(method.type)}
                      <CardTitle className="text-lg">{method.displayName}</CardTitle>
                    </div>
                    <Badge variant={method.isActive ? "default" : "secondary"}>
                      {method.isActive ? "Active" : "Inactive"}
                    </Badge>
                  </div>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div>
                    <p className="text-sm text-muted-foreground">Type: {method.type}</p>
                    <p className="text-sm text-muted-foreground">Fee: {method.processingFeePercent}%</p>
                    {method.description && (
                      <p className="text-sm text-muted-foreground mt-2">{method.description}</p>
                    )}
                  </div>

                  <div className="flex space-x-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => {
                        setEditingMethod(method);
                        setShowMethodDialog(true);
                      }}
                      data-testid={`button-edit-method-${method.id}`}
                    >
                      <Edit className="h-4 w-4 mr-1" />
                      Edit
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => deleteMethodMutation.mutate(method.id)}
                      disabled={deleteMethodMutation.isPending}
                      data-testid={`button-delete-method-${method.id}`}
                    >
                      <Trash2 className="h-4 w-4 mr-1" />
                      Delete
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </TabsContent>

        {/* Payment Configurations Tab */}
        <TabsContent value="configurations" className="space-y-6">
          <div className="flex items-center justify-between">
            <h2 className="text-2xl font-semibold">Fee Structure Configuration</h2>
            <Dialog open={showConfigDialog} onOpenChange={setShowConfigDialog}>
              <DialogTrigger asChild>
                <Button onClick={() => setEditingConfig(null)} data-testid="button-add-config">
                  <Plus className="h-4 w-4 mr-2" />
                  Add Configuration
                </Button>
              </DialogTrigger>
              <DialogContent>
                <DialogHeader>
                  <DialogTitle>
                    {editingConfig ? 'Edit Configuration' : 'Add Configuration'}
                  </DialogTitle>
                </DialogHeader>
                <form onSubmit={handleConfigSubmit} className="space-y-4">
                  <div>
                    <Label htmlFor="configType">Configuration Type</Label>
                    <Select name="configType" defaultValue={editingConfig?.configType}>
                      <SelectTrigger data-testid="select-config-type">
                        <SelectValue placeholder="Select configuration type" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="advance_percentage">Advance Payment %</SelectItem>
                        <SelectItem value="commission_rate">Commission Rate %</SelectItem>
                        <SelectItem value="platform_fee">Platform Fee %</SelectItem>
                        <SelectItem value="gateway_fee">Gateway Fee %</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                  <div>
                    <Label htmlFor="value">Value</Label>
                    <Input
                      id="value"
                      name="value"
                      type="number"
                      step="0.01"
                      defaultValue={editingConfig?.value}
                      placeholder="30.00"
                      required
                      data-testid="input-config-value"
                    />
                  </div>
                  <div>
                    <Label htmlFor="description">Description</Label>
                    <Textarea
                      id="description"
                      name="description"
                      defaultValue={editingConfig?.description}
                      placeholder="Description of this configuration"
                      data-testid="textarea-config-description"
                    />
                  </div>
                  <div className="flex items-center space-x-2">
                    <Switch
                      id="isActive"
                      name="isActive"
                      defaultChecked={editingConfig?.isActive ?? true}
                      data-testid="switch-config-active"
                    />
                    <Label htmlFor="isActive">Active</Label>
                  </div>

                  <div className="flex justify-end space-x-2">
                    <Button
                      type="button"
                      variant="outline"
                      onClick={() => setShowConfigDialog(false)}
                      data-testid="button-cancel-config"
                    >
                      Cancel
                    </Button>
                    <Button
                      type="submit"
                      disabled={createConfigMutation.isPending || updateConfigMutation.isPending}
                      data-testid="button-save-config"
                    >
                      <Save className="h-4 w-4 mr-2" />
                      {editingConfig ? 'Update' : 'Create'}
                    </Button>
                  </div>
                </form>
              </DialogContent>
            </Dialog>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {paymentConfigs.map((config: PaymentConfiguration) => (
              <Card key={config.id}>
                <CardHeader className="pb-3">
                  <div className="flex items-center justify-between">
                    <CardTitle className="text-lg">
                      {getConfigTypeLabel(config.configType)}
                    </CardTitle>
                    <Badge variant={config.isActive ? "default" : "secondary"}>
                      {config.isActive ? "Active" : "Inactive"}
                    </Badge>
                  </div>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div>
                    <p className="text-2xl font-bold text-foreground">
                      {config.value}%
                    </p>
                    {config.description && (
                      <p className="text-sm text-muted-foreground mt-2">{config.description}</p>
                    )}
                  </div>

                  <div className="flex space-x-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => {
                        setEditingConfig(config);
                        setShowConfigDialog(true);
                      }}
                      data-testid={`button-edit-config-${config.id}`}
                    >
                      <Edit className="h-4 w-4 mr-1" />
                      Edit
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </TabsContent>

        {/* Overview Tab */}
        <TabsContent value="overview" className="space-y-6">
          <h2 className="text-2xl font-semibold">Payment System Overview</h2>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <Card>
              <CardHeader className="pb-3">
                <CardTitle className="text-lg flex items-center">
                  <CreditCard className="h-5 w-5 mr-2" />
                  Payment Methods
                </CardTitle>
              </CardHeader>
              <CardContent>
                <p className="text-3xl font-bold text-foreground">
                  {paymentMethods.filter((m: PaymentMethod) => m.isActive).length}
                </p>
                <p className="text-muted-foreground">Active methods</p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="pb-3">
                <CardTitle className="text-lg flex items-center">
                  <Percent className="h-5 w-5 mr-2" />
                  Fee Configurations
                </CardTitle>
              </CardHeader>
              <CardContent>
                <p className="text-3xl font-bold text-foreground">
                  {paymentConfigs.filter((c: PaymentConfiguration) => c.isActive).length}
                </p>
                <p className="text-muted-foreground">Active configurations</p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="pb-3">
                <CardTitle className="text-lg flex items-center">
                  <CheckCircle className="h-5 w-5 mr-2" />
                  System Status
                </CardTitle>
              </CardHeader>
              <CardContent>
                <p className="text-3xl font-bold text-green-600">Ready</p>
                <p className="text-muted-foreground">Payment system operational</p>
              </CardContent>
            </Card>
          </div>

          {/* Current Configuration Summary */}
          <Card>
            <CardHeader>
              <CardTitle>Current Configuration Summary</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {paymentConfigs.filter((c: PaymentConfiguration) => c.isActive).map((config: PaymentConfiguration) => (
                  <div key={config.id} className="flex justify-between items-center p-3 bg-muted rounded-lg">
                    <span className="font-medium">{getConfigTypeLabel(config.configType)}</span>
                    <span className="text-lg font-bold">{config.value}%</span>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/pages/buyer/payments.tsx.content:
import { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { 
  CreditCard, Clock, CheckCircle, XCircle, AlertCircle, 
  Calendar, DollarSign, ExternalLink, Filter, Download, 
  Eye, ArrowUpRight, Wallet, TrendingUp
} from 'lucide-react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { authenticatedApiClient } from '@/lib/supabase';
import { formatCurrency } from '@/lib/utils';
import { format } from 'date-fns';

interface PaymentTransaction {
  id: string;
  transactionRef: string;
  orderId?: string;
  curatedOfferId?: string;
  amount: string;
  fees: string;
  netAmount: string;
  currency: string;
  status: 'pending' | 'processing' | 'completed' | 'failed' | 'cancelled' | 'refunded';
  transactionType: 'advance_payment' | 'final_payment' | 'full_payment' | 'refund' | 'commission';
  notes?: string;
  processedAt?: string;
  createdAt: string;
  updatedAt: string;
}

interface CuratedOffer {
  id: string;
  rfqId: string;
  title: string;
  totalPrice: string;
  paymentLink?: string;
  advancePaymentAmount?: string;
  finalPaymentAmount?: string;
  paymentDeadline?: string;
  paymentTerms?: string;
  publishedAt: string;
  expiresAt?: string;
}

export default function BuyerPaymentDashboard() {
  const [activeTab, setActiveTab] = useState('overview');
  const [selectedOrder, setSelectedOrder] = useState<string | null>(null);
  const [statusFilter, setStatusFilter] = useState<string>('all');

  // Payment Transactions Data
  const { data: paymentTransactions = [] } = useQuery({
    queryKey: ['/api/protected/buyer/payment-transactions'],
    queryFn: () => authenticatedApiClient.get('/api/protected/buyer/payment-transactions'),
  });

  // Orders with payment information
  const { data: orders = [] } = useQuery({
    queryKey: ['/api/protected/orders'],
    queryFn: () => authenticatedApiClient.get('/api/protected/orders'),
  });

  // Filter transactions based on status
  const filteredTransactions = paymentTransactions.filter((tx: PaymentTransaction) => {
    if (statusFilter === 'all') return true;
    return tx.status === statusFilter;
  });

  // Calculate payment statistics
  const paymentStats = {
    totalPaid: paymentTransactions
      .filter((tx: PaymentTransaction) => tx.status === 'completed')
      .reduce((sum: number, tx: PaymentTransaction) => sum + parseFloat(tx.amount), 0),
    totalPending: paymentTransactions
      .filter((tx: PaymentTransaction) => tx.status === 'pending')
      .reduce((sum: number, tx: PaymentTransaction) => sum + parseFloat(tx.amount), 0),
    completedCount: paymentTransactions.filter((tx: PaymentTransaction) => tx.status === 'completed').length,
    pendingCount: paymentTransactions.filter((tx: PaymentTransaction) => tx.status === 'pending').length,
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'completed': return <CheckCircle className="h-4 w-4 text-green-600" />;
      case 'pending': return <Clock className="h-4 w-4 text-yellow-600" />;
      case 'processing': return <ArrowUpRight className="h-4 w-4 text-blue-600" />;
      case 'failed': return <XCircle className="h-4 w-4 text-red-600" />;
      case 'cancelled': return <XCircle className="h-4 w-4 text-gray-600" />;
      default: return <AlertCircle className="h-4 w-4 text-gray-600" />;
    }
  };

  const getStatusBadge = (status: string) => {
    const variants: Record<string, "default" | "secondary" | "destructive" | "outline"> = {
      completed: "default",
      pending: "secondary", 
      processing: "outline",
      failed: "destructive",
      cancelled: "secondary"
    };
    return variants[status] || "secondary";
  };

  const getTransactionTypeLabel = (type: string) => {
    switch (type) {
      case 'advance_payment': return 'Advance Payment';
      case 'final_payment': return 'Final Payment';
      case 'full_payment': return 'Full Payment';
      case 'refund': return 'Refund';
      default: return type;
    }
  };

  // Using shared formatCurrency utility from @/lib/utils

  return (
    <div className="p-8 space-y-8">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-foreground">Payment Dashboard</h1>
          <p className="text-muted-foreground mt-2">Track your payments, view history, and manage transactions</p>
        </div>
        <div className="flex space-x-2">
          <Button variant="outline" data-testid="button-download-statement">
            <Download className="h-4 w-4 mr-2" />
            Download Statement
          </Button>
        </div>
      </div>

      {/* Payment Statistics Overview */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-muted-foreground text-sm">Total Paid</p>
                <p className="text-2xl font-bold text-green-600" data-testid="text-total-paid">
                  {formatCurrency(paymentStats.totalPaid.toString())}
                </p>
              </div>
              <CheckCircle className="h-8 w-8 text-green-600" />
            </div>
            <p className="text-green-600 text-sm mt-2">{paymentStats.completedCount} completed</p>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-muted-foreground text-sm">Amount Pending</p>
                <p className="text-2xl font-bold text-yellow-600" data-testid="text-total-pending">
                  {formatCurrency(paymentStats.totalPending.toString())}
                </p>
              </div>
              <Clock className="h-8 w-8 text-yellow-600" />
            </div>
            <p className="text-yellow-600 text-sm mt-2">{paymentStats.pendingCount} pending</p>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-muted-foreground text-sm">This Month</p>
                <p className="text-2xl font-bold text-foreground" data-testid="text-monthly-amount">
                  {formatCurrency("45000")}
                </p>
              </div>
              <TrendingUp className="h-8 w-8 text-blue-600" />
            </div>
            <p className="text-green-600 text-sm mt-2">+12% from last month</p>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-muted-foreground text-sm">Payment Methods</p>
                <p className="text-2xl font-bold text-foreground" data-testid="text-payment-methods">3</p>
              </div>
              <Wallet className="h-8 w-8 text-purple-600" />
            </div>
            <p className="text-muted-foreground text-sm mt-2">Available options</p>
          </CardContent>
        </Card>
      </div>

      {/* Payment Dashboard Tabs */}
      <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-6">
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="overview" data-testid="tab-payment-overview">Overview</TabsTrigger>
          <TabsTrigger value="pending" data-testid="tab-pending-payments">Pending Payments</TabsTrigger>
          <TabsTrigger value="history" data-testid="tab-payment-history">Payment History</TabsTrigger>
          <TabsTrigger value="methods" data-testid="tab-payment-methods">Payment Methods</TabsTrigger>
        </TabsList>

        {/* Overview Tab */}
        <TabsContent value="overview" className="space-y-6">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Recent Transactions */}
            <Card>
              <CardHeader>
                <CardTitle className="text-xl">Recent Transactions</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                {paymentTransactions.slice(0, 5).map((transaction: PaymentTransaction) => (
                  <div key={transaction.id} className="flex items-center justify-between p-3 border border-border rounded-lg">
                    <div className="flex items-center space-x-3">
                      {getStatusIcon(transaction.status)}
                      <div>
                        <p className="font-medium text-foreground">{transaction.transactionRef}</p>
                        <p className="text-sm text-muted-foreground">
                          {getTransactionTypeLabel(transaction.transactionType)}
                        </p>
                      </div>
                    </div>
                    <div className="text-right">
                      <p className="font-bold text-foreground">{formatCurrency(transaction.amount)}</p>
                      <Badge variant={getStatusBadge(transaction.status)} className="text-xs">
                        {transaction.status}
                      </Badge>
                    </div>
                  </div>
                ))}
                {paymentTransactions.length === 0 && (
                  <div className="text-center py-8">
                    <CreditCard className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                    <p className="text-muted-foreground">No transactions yet</p>
                  </div>
                )}
              </CardContent>
            </Card>

            {/* Action Items */}
            <Card>
              <CardHeader>
                <CardTitle className="text-xl">Action Required</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                {/* Pending payments that need action */}
                {paymentTransactions
                  .filter((tx: PaymentTransaction) => tx.status === 'pending')
                  .slice(0, 3)
                  .map((transaction: PaymentTransaction) => (
                  <div key={transaction.id} className="p-4 bg-yellow-50 dark:bg-yellow-900/20 border border-yellow-200 dark:border-yellow-800 rounded-lg">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="font-medium text-foreground">Payment Due</p>
                        <p className="text-sm text-muted-foreground">
                          {getTransactionTypeLabel(transaction.transactionType)} - {formatCurrency(transaction.amount)}
                        </p>
                        <p className="text-xs text-muted-foreground mt-1">
                          Due: {format(new Date(transaction.createdAt), 'MMM dd, yyyy')}
                        </p>
                      </div>
                      <Button size="sm" data-testid={`button-pay-${transaction.id}`}>
                        <ExternalLink className="h-4 w-4 mr-1" />
                        Pay Now
                      </Button>
                    </div>
                  </div>
                ))}

                {paymentTransactions.filter((tx: PaymentTransaction) => tx.status === 'pending').length === 0 && (
                  <div className="text-center py-8">
                    <CheckCircle className="h-12 w-12 text-green-600 mx-auto mb-4" />
                    <p className="text-muted-foreground">All payments are up to date!</p>
                  </div>
                )}
              </CardContent>
            </Card>
          </div>
        </TabsContent>

        {/* Pending Payments Tab */}
        <TabsContent value="pending" className="space-y-6">
          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <CardTitle className="text-xl">Pending Payments</CardTitle>
                <Badge variant="secondary">{paymentStats.pendingCount} pending</Badge>
              </div>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {paymentTransactions
                  .filter((tx: PaymentTransaction) => tx.status === 'pending')
                  .map((transaction: PaymentTransaction) => (
                  <Card key={transaction.id} className="border-l-4 border-l-yellow-500">
                    <CardContent className="p-6">
                      <div className="flex items-center justify-between">
                        <div className="space-y-2">
                          <div className="flex items-center space-x-2">
                            <Badge variant="outline">{transaction.transactionRef}</Badge>
                            <Badge variant="secondary">{getTransactionTypeLabel(transaction.transactionType)}</Badge>
                          </div>
                          <p className="text-2xl font-bold text-foreground">{formatCurrency(transaction.amount)}</p>
                          <p className="text-sm text-muted-foreground">
                            Created: {format(new Date(transaction.createdAt), 'MMM dd, yyyy')}
                          </p>
                          {transaction.notes && (
                            <p className="text-sm text-muted-foreground">{transaction.notes}</p>
                          )}
                        </div>
                        <div className="flex space-x-2">
                          <Button variant="outline" size="sm" data-testid={`button-view-${transaction.id}`}>
                            <Eye className="h-4 w-4 mr-1" />
                            View Details
                          </Button>
                          <Button size="sm" data-testid={`button-pay-now-${transaction.id}`}>
                            <ExternalLink className="h-4 w-4 mr-1" />
                            Pay Now
                          </Button>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))}

                {paymentStats.pendingCount === 0 && (
                  <div className="text-center py-12">
                    <CheckCircle className="h-16 w-16 text-green-600 mx-auto mb-4" />
                    <h3 className="text-lg font-semibold text-foreground mb-2">All Caught Up!</h3>
                    <p className="text-muted-foreground">You have no pending payments at this time.</p>
                  </div>
                )}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Payment History Tab */}
        <TabsContent value="history" className="space-y-6">
          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <CardTitle className="text-xl">Payment History</CardTitle>
                <div className="flex space-x-2">
                  <Select value={statusFilter} onValueChange={setStatusFilter}>
                    <SelectTrigger className="w-40" data-testid="select-status-filter">
                      <SelectValue placeholder="Filter by status" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All Status</SelectItem>
                      <SelectItem value="completed">Completed</SelectItem>
                      <SelectItem value="pending">Pending</SelectItem>
                      <SelectItem value="failed">Failed</SelectItem>
                      <SelectItem value="cancelled">Cancelled</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>
            </CardHeader>
            <CardContent>
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Transaction Ref</TableHead>
                    <TableHead>Type</TableHead>
                    <TableHead>Amount</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead>Date</TableHead>
                    <TableHead>Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {filteredTransactions.map((transaction: PaymentTransaction) => (
                    <TableRow key={transaction.id}>
                      <TableCell className="font-medium">{transaction.transactionRef}</TableCell>
                      <TableCell>
                        <Badge variant="outline">
                          {getTransactionTypeLabel(transaction.transactionType)}
                        </Badge>
                      </TableCell>
                      <TableCell className="font-bold">{formatCurrency(transaction.amount)}</TableCell>
                      <TableCell>
                        <div className="flex items-center space-x-2">
                          {getStatusIcon(transaction.status)}
                          <Badge variant={getStatusBadge(transaction.status)}>
                            {transaction.status}
                          </Badge>
                        </div>
                      </TableCell>
                      <TableCell>{format(new Date(transaction.createdAt), 'MMM dd, yyyy')}</TableCell>
                      <TableCell>
                        <Button 
                          variant="ghost" 
                          size="sm"
                          data-testid={`button-view-transaction-${transaction.id}`}
                        >
                          <Eye className="h-4 w-4" />
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>

              {filteredTransactions.length === 0 && (
                <div className="text-center py-12">
                  <CreditCard className="h-16 w-16 text-muted-foreground mx-auto mb-4" />
                  <h3 className="text-lg font-semibold text-foreground mb-2">No Transactions Found</h3>
                  <p className="text-muted-foreground">
                    {statusFilter === 'all' 
                      ? 'You haven\'t made any payments yet.' 
                      : `No ${statusFilter} transactions found.`}
                  </p>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        {/* Payment Methods Tab */}
        <TabsContent value="methods" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle className="text-xl">Available Payment Methods</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {/* Sample payment methods */}
                <div className="p-6 border border-border rounded-lg">
                  <div className="flex items-center space-x-3 mb-4">
                    <CreditCard className="h-8 w-8 text-blue-600" />
                    <div>
                      <h3 className="font-semibold text-foreground">Credit/Debit Card</h3>
                      <p className="text-sm text-muted-foreground">Via Razorpay</p>
                    </div>
                  </div>
                  <p className="text-sm text-muted-foreground mb-4">
                    Secure payment processing with all major credit and debit cards
                  </p>
                  <Badge variant="default">Active</Badge>
                </div>

                <div className="p-6 border border-border rounded-lg">
                  <div className="flex items-center space-x-3 mb-4">
                    <Wallet className="h-8 w-8 text-green-600" />
                    <div>
                      <h3 className="font-semibold text-foreground">UPI</h3>
                      <p className="text-sm text-muted-foreground">Instant payments</p>
                    </div>
                  </div>
                  <p className="text-sm text-muted-foreground mb-4">
                    Pay instantly using UPI apps like PhonePe, Google Pay, Paytm
                  </p>
                  <Badge variant="default">Active</Badge>
                </div>

                <div className="p-6 border border-border rounded-lg">
                  <div className="flex items-center space-x-3 mb-4">
                    <DollarSign className="h-8 w-8 text-purple-600" />
                    <div>
                      <h3 className="font-semibold text-foreground">Bank Transfer</h3>
                      <p className="text-sm text-muted-foreground">NEFT/RTGS</p>
                    </div>
                  </div>
                  <p className="text-sm text-muted-foreground mb-4">
                    Direct bank transfer for large transactions
                  </p>
                  <Badge variant="default">Active</Badge>
                </div>
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

